/**
 3. Using the REST API at 'localhost:3000/students' create a web application for managing students
 The REST API provides methods as follows:
 POST creates a new student
 GET returns all students
 DELETE deletes a student by Id
 You may extend the demo for jQuery.ajax()
 */

// Generated by CoffeeScript 1.7.1
(function () {
    'use strict';

    var $errorMessage, $successMessage, addStudent, deleteStudent, reloadStudents, resourceUrl, deleteUrl;

    resourceUrl = 'http://localhost:3000/students';

    $successMessage = $('.messages .success');

    $errorMessage = $('.messages .error');

    addStudent = function (data) {
        return $.ajax({
            url: resourceUrl,
            type: 'POST',
            data: JSON.stringify(data),
            contentType: 'application/json',
            success: function (data) {
                console.log('aaaaaaaaaaaaaaaaaaaaaaaaaaa');
                $successMessage
                    .html('' + data.name + ' successfully added')
                    .show()
                    .fadeOut(2000);
                reloadStudents();
            },
            error: function (err) {
                $errorMessage
                    .html('Error happened: ' + err)
                    .show()
                    .fadeOut(2000);
            }
        });
    };

    deleteStudent = function (data) {
        return $.ajax({
            url: resourceUrl + '/' + data.id + '/',
            type: 'POST',
            data: {_method: 'delete'},
            success: function (data) {
                console.log(data);
                $successMessage
                    .html('successfully deleted')
                    .show()
                    .fadeOut(2000);
                reloadStudents();
            },
            error: function (err) {
                $errorMessage
                    .html('Error happened: ' + err)
                    .show()
                    .fadeOut(2000);
            }
        });
    };

    reloadStudents = function () {
        $.ajax({
            url: resourceUrl,
            type: 'GET',
            contentType: 'application/json',
            success: function (data) {
                var student, $studentsList, i, len;

                $studentsList = $('<ul/>').addClass('students-list');

                for (i = 0, len = data.students.length; i < len; i++) {
                    student = data.students[i];

                    $('<li />')
                        .addClass('student-item')
                        .append($('<strong /> ')
                            .html(student.name))
                        .append($('<span />')
                            .html(student.grade))
                        .append($('<span />')
                            .html(student.id))
                        .appendTo($studentsList);
                }
                $('#students-container').html($studentsList);
            },
            error: function () {
                $errorMessage
                    .html("Error happened: " + err)
                    .show()
                    .fadeOut(2000);
            }
        });
    };

    $(function () {
        reloadStudents();
        $('#btn-add-student').on('click', function () {
            var student;
            student = {
                name: $('#tb-name').val(),
                grade: $('#tb-grade').val()
            };

            addStudent(student);
        });
        $('#btn-delete-student').on('click', function () {
            var student = {
                id: $('#tb-id').val()
            };

            deleteStudent(student);
        });
    });

}).call(this);

